import shelfFile from "./images/shelf.png";
import interact from 'interact.js';
import wateringCanFile from "./images/watering-can.png";
export default {
  name: 'shelf',
  data: function data() {
    return {
      shelfImage: shelfFile,
      wateringCanImage: wateringCanFile
    };
  },
  mounted: function mounted() {
    var shelf = document.getElementById('shelf');
    interact('.draggable').draggable({
      onmove: this.dragMoveListener,
      inertia: true,
      restrict: {
        restriction: shelf,
        elementRect: {
          top: .90,
          left: 0,
          bottom: 1.1,
          right: 1
        },
        endOnly: true
      }
    });
  },
  methods: {
    dragMoveListener: function dragMoveListener(event) {
      var target = event.target,
          x = (parseFloat(target.getAttribute('data-x')) || 0) + event.dx,
          y = (parseFloat(target.getAttribute('data-y')) || 0) + event.dy; // translate the element

      target.style.webkitTransform = 'translate(' + x + 'px, ' + y + 'px)';
      target.style.transform = 'translate(' + x + 'px, ' + y + 'px)'; // update the posiion attributes

      target.setAttribute('data-x', x);
      target.setAttribute('data-y', y);
    }
  }
};
